mapper.update:
UPDATE transaction
payment_option = #{paymentOption} , receiver_name = #{receiverName}, demailaddr = #{divyAddr} , receiver_phone = #{receiverPhone}, dlvy_request = #{divyRequest}, dlvy_date = #{divyDate}
WHERE tran_no = #{tranNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT *
					FROM transaction
						WHERE buyer_id = #{userId}	
					 ) countTable
